-- Integrated three datasets from a Big Data Project
-- Use SQL to answer the questions I have already solved in Python

CREATE DATABASE IF NOT EXISTS sql_bigdata;

USE sql_bigdata;

CREATE TABLE orders(
   order_id     INTEGER  NOT NULL PRIMARY KEY,
   week         INTEGER  NOT NULL,
   article_id   INTEGER  NOT NULL,
   quantity     INTEGER  NOT NULL,
   seller_id    INTEGER  NOT NULL,
   country_name VARCHAR(11) NOT NULL
);

SELECT * FROM orders;

-- DROP TABLE sellers;

CREATE TABLE sellers(
   seller_id   INTEGER  NOT NULL PRIMARY KEY, 
   seller_name VARCHAR(17) NOT NULL
);


SELECT * FROM sellers;

CREATE TABLE IF NOT EXISTS articles(
   article_id   INTEGER  NOT NULL PRIMARY KEY 
  ,article_name VARCHAR(16) NOT NULL
  ,unit_price   NUMERIC(7,2) NOT NULL
);

SELECT * FROM articles;

-- 	Combine certain cols of two tables 
SELECT orders.seller_id, seller_name, quantity, country_name 
FROM orders
INNER JOIN sellers
WHERE sellers.seller_id = orders.seller_id;

-- Best seller of the month (in units)
SELECT seller_name, sum(quantity) AS total_quantity
FROM orders
INNER JOIN sellers
ON sellers.seller_id = orders.seller_id
GROUP BY seller_name
ORDER BY sum(quantity) DESC;
-- Answer: Janel O'Curran

-- Quantity by country
SELECT country_name, sum(quantity) AS total_quantity
FROM orders
GROUP BY country_name
ORDER BY sum(quantity) DESC;

-- Best 5 selling article (in units)
SELECT article_name, sum(quantity) AS total_quantity
FROM orders
INNER JOIN articles
ON articles.article_id = orders.article_id
GROUP BY article_name
ORDER BY sum(quantity) DESC LIMIT 5;
-- Answer: HDD, Tablet, SDD, Mouse, Netbook

-- Article with the highest income
SELECT article_name, sum(quantity * unit_price) AS total_amount
FROM orders
INNER JOIN articles
ON articles.article_id = orders.article_id
GROUP BY article_name
ORDER BY sum(quantity * unit_price) DESC LIMIT 1;
-- Answer: Full PC

-- Best 3 countries based on the total sales amount
SELECT country_name, sum(quantity * unit_price) AS total_amount
FROM orders
INNER JOIN articles
ON articles.article_id = orders.article_id
GROUP BY country_name
ORDER BY sum(quantity * unit_price) DESC LIMIT 3;
-- Answer: Brazil, Argentina, Colombia

-- Of the best country (Brazil): top three articles and their total sales amount
SELECT country_name, article_name, sum(quantity * unit_price) AS total_amount
FROM orders
INNER JOIN articles
ON articles.article_id = orders.article_id
GROUP BY article_name, country_name
HAVING country_name = 'Brazil'
ORDER BY sum(quantity * unit_price) DESC LIMIT 3;
-- Answer: Full PC (134052.03), Notebook (83000) and Smartphone (41475)

-- Best week based on total sales amount
SELECT week, sum(quantity * unit_price) AS total_amount
FROM orders
INNER JOIN articles
ON articles.article_id = orders.article_id
GROUP BY week
ORDER BY sum(quantity * unit_price) DESC;
-- Answer: week 1

-- Combine and save the three tables and calculate total amount
CREATE TABLE my_df
SELECT article_name, quantity, quantity * unit_price AS total_amount, seller_name, country_name, week
FROM orders
INNER JOIN articles
ON  orders.article_id = articles.article_id
INNER JOIN sellers
ON orders.seller_id = sellers.seller_id;

SELECT * FROM my_df;
SELECT COUNT(*) AS total_rows FROM my_df;
